import React, { Component } from 'react';

class Storage extends Component {
    state=
    {
        data:"",
        count:0
    };
    // constructor(props)
    // {
    //     super(props)
    //     this.setData=this.setData.bind(this);
    //     this.getData=this.getData.bind(this);
    // }
    counterIncrement=()=>
    {
        this.setState({count:this.state.count+1})
    }
    counterWithArgument=(a)=>
    {
        this.setState({count:a})
    }
    setData=()=>
    {
        localStorage.setItem("myData","Some data");
    }
    getData=()=>
    {
        this.state.data=localStorage.getItem("myData");
        console.log(this.state.data);
    }
    render() { 
        return ( 
            <div>
            <div className="Storage_Button">
                <button onClick ={this.setData}>Set Data</button>
            </div>
            <div className="Display Data">
                <button onClick ={this.getData}>Get Data</button>
        <h1>Some Data++++++++{this.state.data}</h1>
            </div>
            <div>
        <p>{this.state.count}</p>
                <button onClick={this.counterIncrement}>Press To Increment Counter</button>
                <button onClick={()=>this.counterWithArgument(10)}>Press To Pass Argument To Counter</button>
            </div>
            </div>
         );
    }
}
 
export default Storage;
