import React, { Component } from 'react';

class Exercise_1 extends Component {
    state = { 
        number_of_rows:3
     };

     delete_row1=()=>
     {
        document.getElementById('td-1').style.display="none";
        this.setState({number_of_rows:this.state.number_of_rows-1})
     }
     delete_row2=()=>
     {
        document.getElementById('td-2').style.display="none";
     }
     delete_row3=()=>
     {
        document.getElementById('td-3').style.display="none";
     }
    render() { 
        return ( 
            <div>
                <h1>There are {this.state.number_of_rows} rows present</h1>
                <table>
                    <tr>
                        <th>Name</th>
                        <th>Place</th>
                        <th>Animal</th>
                    </tr>
                    <tr id="td-1">
                        <td>1</td>
                        <td>1</td>
                        <td>1</td>
                        <td><button onClick={this.delete_row1}>DELETE</button></td>
                    </tr>
                    <tr id="td-2">
                        <td>2</td>
                        <td>2</td>
                        <td>2</td>
                        <td><button onClick={this.delete_row2}>DELETE</button></td>
                    </tr>
                    <tr id="td-3"> 
                        <td>3</td>
                        <td>3</td>
                        <td>3</td>
                        <td><button onClick={this.delete_row3}>DELETE</button></td>
                    </tr>
                </table>
            </div>
         );
    }
}
 
export default Exercise_1;
