import React, { Component } from 'react';

class Exercise_1 extends Component {
    state = { 
        number_of_rows:3
     };

     delete_row1=()=>
     {
        document.getElementsByClassName("td-1")
     }
     delete_row2=()=>
     {
        document.getElementsByClassName('td-2')
     }
     delete_row3=()=>
     {
        document.getElementsByClassName('td-3')
     }
    render() { 
        return ( 
            <div>
                <h1>There are {this.state.number_of_rows} rows present</h1>
                <table>
                    <tr>
                        <th>Name</th>
                        <th>Place</th>
                        <th>Animal</th>
                    </tr>
                    <tr className="td-1">
                        <td>abc</td>
                        <td>cde</td>
                        <td>efg</td>
                        <td><button onClick={this.delete_row1}>DELETE</button></td>
                    </tr>
                    <tr className="td-2">
                        <td>abc</td>
                        <td>cde</td>
                        <td>efg</td>
                        <td><button onClick={this.delete_row2}>DELETE</button></td>
                    </tr>
                    <tr className="td-3"> 
                        <td>abc</td>
                        <td>cde</td>
                        <td>efg</td>
                        <td><button onClick={this.delete_row3}>DELETE</button></td>
                    </tr>
                </table>
            </div>
         );
    }
}
 
export default Exercise_1;
